def find_largest_visitors(data):
    max_visitors = 0
    largest_website = ""
    
    # Recursive function to explore each child node
    def parse_children(children):
        nonlocal max_visitors, largest_website
        for child in children:
            if 'children' in child:
                parse_children(child['children'])  # Recursive call for nested children
            else:
                # We are at a leaf node with website info
                if child['value'] > max_visitors:
                    max_visitors = child['value']
                    largest_website = child['name']

    parse_children(data['children'])
    return largest_website, max_visitors

