# Function to search for visitors in a nested structure
def find_visitors(data, website_name):
    if isinstance(data, list):
        for item in data:
            result = find_visitors(item, website_name)
            if result:
                return result
    elif isinstance(data, dict):
        if data.get('name') == website_name:
            return data.get('value')
        if 'children' in data:
            return find_visitors(data['children'], website_name)
    return None

